    fun findSubstring2(s: String = this.s, words: Array<String> = this.words): List<Int> {
        val wcount = words.count()
        val wlength = words[0].length
        val output = mutableListOf<Int>()
        for (i in 0 until s.length + 1 - wlength * wcount) {
            var sum = 0
            val ifilter = words.toMutableList()
            ifilter.sort()
            var iarray = ifilter.toTypedArray()
            for (x in 0 until wcount) {
                val firstword = s.substring(i + wlength * sum, i + wlength * (sum + 1))
                val wordindex = i + wlength * (sum + 1)
                if (iarray.binarySearch(firstword,0,iarray.size)>=0 && wordindex <= s.length) {
                    ifilter.remove(firstword)
                    iarray = ifilter.toTypedArray()
                    sum++
                } else {
                    break
                }
            }
            if (sum == wcount)
                output.add(i)
        }
        return  output
    }

    fun findSubstring(s: String = this.s, words: Array<String> = this.words): List<Int> {
        words.sort()
        val wcount = words.count()
        val wlength = words[0].length
        val output = mutableListOf<Int>()
        val slen = s.length
        //Небольшой лайфхак
        if (wcount == slen)
            return if (s.toSet().first().toString() == words.toSet().first())
                listOf(0)
            else emptyList<Int>()
        val maxlen = slen + 1 - wlength * wcount
        var blacklist = hashMapOf<Int, String>()
        //для каждого элемента строки
        for (i in 0 until maxlen) {
            //Берем следующее слово с текущей позиции в строке
            var nextword = s.substring(i, i + wlength)
            //Ищем следующее слово в заданном массиве
            var bs = words.binarySearch(nextword, 0, words.size)
            if (words[0] == nextword) bs = 0
            //Если находим слово - обрабатываем, иначе берем следующую позицию строки
            if (bs >= 0) {
                //Добавляем индекс массива найденного слова в черный список
                blacklist.put(bs, nextword)
                var iterator = 1
                while (true) {
                    //Если количество найденных элементов соответствует количеству слов в массиве
                    if (blacklist.size == wcount) {
                        output.add(i)
                        break
                    }
                    //Определяем следующее слово
                    val wordindex = i + wlength * (iterator + 1)
                    //Проверка на выход за границы массива
                    if (wordindex > s.length)
                        break
                    nextword = s.substring(i + wlength * iterator, wordindex)
                    iterator++
                    //Если слово есть в черном списке
                    if (blacklist.containsValue(nextword)) {
                        //Определяем максимальный индекс для слова
                        val keys = blacklist.filterValues { it == nextword }.keys
                        val key = keys?.max()
                        if (key!! < 2) {
                            val b = words.withIndex().find { it.index > key && it.value == nextword }
                            bs = (b?.index ?: break)
                        } else {
                            //Ищем слово в массиве с индекса
                            bs = words.binarySearch(nextword, key + 1, words.size)
                        }
                        if (bs > 0) {
                            blacklist.put(bs, nextword)
                        } else
                            break
                    } else {
                        //Иначе ищем слово с самого начала
                        bs = words.binarySearch(nextword, 0, words.size)
                        if (words[0] == nextword) bs = 0
                        if (bs >= 0) {
                            blacklist.put(bs, nextword)
                        } else
                            break
                    }
                }
                blacklist.clear()
            }
        }
        return output
    }
